const is = require('@sindresorhus/is');

const compare = function(obj1, obj2, depth) {
  for (var key in obj2) {
    if (obj2.hasOwnProperty(key)) {
      // if it's same type, compare it
      if (obj1[key] && obj1[key].constructor === obj2[key].constructor) {
        const value1 = obj1[key];
        const value2 = obj2[key];
        if (is.string(value1) && value1 === value2) {
          continue;
        } else if (is.array(value1)) {
          // do some array stuff
        }
      }
    }
    return false;
  }
  return true;
}

module.exports = function(obj1, obj2, depth) {
  depth = depth ? depth : 1;
  return compare(obj1, obj2, depth);
};
