import React from 'react';
import {SketchPicker} from 'react-color';

const Palette = ({color, onChange}) => {
  const popover = {
    position: 'absolute',
    zIndex: '2',
  };
  return (
    <div style={popover}>
      <SketchPicker color={color}
                    onChange={onChange}/>
    </div>
  )
}

export default Palette;